import React, { useState } from 'react';
import { Slider } from 'primereact/slider';
import { Dropdown } from 'primereact/dropdown';
import { TabView, TabPanel } from 'primereact/tabview';
import { InputSwitch } from 'primereact/inputswitch';
import PrimeComponentShowcase from './PrimeComponentShowcase';
import PrimeThemeTokens from './PrimeThemeTokens';
import FigmaMCPHeader from './components/FigmaMCPHeader';
import FigmaMCPDialog from './components/FigmaMCPDialog';
import FigmaMCPGlobalNav from './components/FigmaMCPGlobalNav';
import { Button } from 'primereact/button';
import { primeThemes } from './themeList';
import 'primereact/resources/primereact.min.css';
import 'primeicons/primeicons.css';

const fontOptions = [
  { label: 'System Default', value: 'system-ui, sans-serif' },
  { label: 'Arial Narrow', value: 'Arial Narrow, Arial, sans-serif' },
  { label: 'Merriweather (Serif)', value: 'Merriweather, serif' },
  { label: 'Inter (Sans)', value: 'Inter, Arial, sans-serif' },
];

export default function PrimeDemoTabs() {
  // Find all unique base theme families (e.g. lara-blue, lara-green, etc.)
  const themeRegex = /^(lara|arya|vela|soho|saga|md|mdc|bootstrap4|luna|nano|nova|rhea|mira|fluent|tailwind|viva)-(light|dark)-?(.*)$/;

  // Filter themes to only include those with both light and dark variants
  const getPairedThemes = () => {
    const lightThemes = primeThemes.filter(t => t.includes('-light-'));
    const darkThemes = primeThemes.filter(t => t.includes('-dark-'));

    // Find themes that have both light and dark variants
    const pairedThemes = [];
    lightThemes.forEach(lightTheme => {
      const family = lightTheme.split('-')[0];
      const color = lightTheme.split('-').slice(2).join('-');
      const darkTheme = `${family}-dark-${color}`;

      if (darkThemes.includes(darkTheme)) {
        pairedThemes.push(lightTheme, darkTheme);
      }
    });

    return pairedThemes.sort();
  };

  const pairedThemes = getPairedThemes();
  const [theme, setTheme] = useState(pairedThemes[0] || 'lara-light-blue');
  const [radius, setRadius] = useState(8); // default border radius in px
  const [fontFamily, setFontFamily] = useState(fontOptions[0].value); // default to System Default
  // Determine if current theme is dark
  const isDark = theme.includes('-dark-');

  // When toggling, switch to the corresponding light/dark variant
  const handleToggleDark = (checked) => {
    console.log('Toggle clicked:', checked, 'Current theme:', theme);

    if (checked && theme.includes('-light-')) {
      // Switch to dark theme
      const darkTheme = theme.replace('-light-', '-dark-');
      if (pairedThemes.includes(darkTheme)) {
        console.log('Switching to dark theme:', darkTheme);
        setTheme(darkTheme);
      }
    } else if (!checked && theme.includes('-dark-')) {
      // Switch to light theme
      const lightTheme = theme.replace('-dark-', '-light-');
      if (pairedThemes.includes(lightTheme)) {
        console.log('Switching to light theme:', lightTheme);
        setTheme(lightTheme);
      }
    }
  };

  React.useEffect(() => {
    // Remove any existing theme link
    const prev = document.getElementById('prime-theme-link');
    if (prev) prev.remove();
    // Add new theme link
    const link = document.createElement('link');
    link.rel = 'stylesheet';
    link.id = 'prime-theme-link';
    link.href = `https://unpkg.com/primereact/resources/themes/${theme}/theme.css`;
    document.head.appendChild(link);

    // Always set border radius immediately (in case theme loads instantly)
    document.documentElement.style.setProperty('--border-radius', `${radius}px`);

    // Inject a style tag to force border-radius everywhere
    let styleTag = document.getElementById('force-border-radius');
    if (!styleTag) {
      styleTag = document.createElement('style');
      styleTag.id = 'force-border-radius';
      document.head.appendChild(styleTag);
    }
    styleTag.innerHTML = `.p-button { border-radius: var(--border-radius) !important; }`;

    // Set the font family variable globally
    document.documentElement.style.setProperty('--font-family', fontFamily);

    // Set the border radius variable globally after theme loads
    const setRadiusVar = () => {
      document.documentElement.style.setProperty('--border-radius', `${radius}px`);
    };
    link.addEventListener('load', setRadiusVar);
    // Fallback: if link never loads, still set after a short delay
    setTimeout(setRadiusVar, 1000);
    // Cleanup
    return () => {
      link.removeEventListener('load', setRadiusVar);
    };
  }, [theme, radius, fontFamily]);

  return (
    <div style={{ minHeight: '100vh', background: 'var(--surface-ground)', fontFamily: 'var(--font-family)' }}>
      <div style={{ padding: '1rem', display: 'flex', alignItems: 'center', gap: 16 }}>
        <span style={{ marginLeft: 32, fontWeight: 600 }}>Font Family:</span>
        <Dropdown value={fontFamily} options={fontOptions} onChange={e => setFontFamily(e.value)} style={{ width: 220 }} placeholder="Select Font" />
        <span style={{ fontWeight: 600 }}>Theme:</span>
        <Dropdown value={theme} options={pairedThemes.map(t => ({ label: t, value: t }))} onChange={e => setTheme(e.value)} style={{ width: 260 }} placeholder="Select Theme" />
        <span style={{ fontWeight: 600 }}>Dark Mode:</span>
        <InputSwitch checked={isDark} onChange={handleToggleDark} />
        <span style={{ marginLeft: 32, fontWeight: 600 }}>Border Radius:</span>
        <div style={{ width: 160 }}>
          <Slider min={0} max={32} value={radius} onChange={e => setRadius(e.value)} step={1} style={{ width: 140 }} />
        </div>
        <span style={{ width: 32, textAlign: 'right', fontFamily: 'monospace' }}>{radius}px</span>
      </div>
      <TabView>
        <TabPanel header="Component Showcase">
          <PrimeComponentShowcase />
        </TabPanel>
        <TabPanel header="Theme Tokens & Visualizer">
          <PrimeThemeTokens />
        </TabPanel>
        <TabPanel header="Figma MCP Component">
          <div className="p-4">
            <h2 className="text-2xl font-bold mb-4">Figma MCP Selected Component</h2>
            <p className="text-gray-600 mb-4">
              This component was built using Figma MCP from your currently selected design in Figma.
            </p>
            <FigmaMCPHeader />
          </div>
        </TabPanel>
        <TabPanel header="Figma MCP Dialog">
          <div className="p-4">
            <h2 className="text-2xl font-bold mb-4">Figma MCP Dialog Component</h2>
            <p className="text-gray-600 mb-4">
              This dialog component was built using Figma MCP from your selected dialog design in Figma.
            </p>
            <FigmaMCPDialog />
          </div>
        </TabPanel>
        <TabPanel header="Figma MCP Global Nav">
          <div className="p-4">
            <h2 className="text-2xl font-bold mb-4">Figma MCP Global Navigation</h2>
            <p className="text-gray-600 mb-4">
              This Global Navigation component was built using Figma MCP from your selected navigation design in Figma.
            </p>
            <FigmaMCPGlobalNav />
          </div>
        </TabPanel>
      </TabView>
    </div>
  );
}
