import React, { useState } from 'react';
import { Menubar } from 'primereact/menubar';
import { TabView, TabPanel } from 'primereact/tabview';
import { Avatar } from 'primereact/avatar';
import { Button } from 'primereact/button';
import { Card } from 'primereact/card';
import { Dropdown } from 'primereact/dropdown';
import { Checkbox } from 'primereact/checkbox';
import { Divider } from 'primereact/divider';

const FigmaMCPGlobalNav = () => {
  const [activeTab, setActiveTab] = useState(0);
  const [navConfig, setNavConfig] = useState({
    color: 'Light',
    header: true,
    megaMenu: false,
    menu: false,
    leftToggleButtons: false,
    avatar: true,
    rightButton: false,
    logOut: false,
    tabs: true
  });

  const colorOptions = [
    { label: 'Light', value: 'Light' },
    { label: 'Dark', value: 'Dark' }
  ];

  const tabItems = [
    { label: 'Tab 1', icon: 'pi pi-home' },
    { label: 'Tab 2', icon: 'pi pi-user' },
    { label: 'Tab 3', icon: 'pi pi-cog' },
    { label: 'Tab 4', icon: 'pi pi-chart' },
    { label: 'Tab 5', icon: 'pi pi-bell' }
  ];

  const handleConfigChange = (key, value) => {
    setNavConfig(prev => ({
      ...prev,
      [key]: value
    }));
  };

  // Create menubar items for the navigation
  const createMenubarItems = () => {
    const items = [];

    if (navConfig.header) {
      items.push({
        label: 'Product',
        icon: 'pi pi-th-large',
        className: 'font-bold'
      });
    }

    if (navConfig.megaMenu) {
      items.push({
        label: 'Mega Menu',
        icon: 'pi pi-bars',
        items: [
          { label: 'Submenu 1', icon: 'pi pi-angle-right' },
          { label: 'Submenu 2', icon: 'pi pi-angle-right' }
        ]
      });
    }

    if (navConfig.menu) {
      items.push({
        label: 'Menu',
        icon: 'pi pi-list',
        items: [
          { label: 'Option 1', icon: 'pi pi-check' },
          { label: 'Option 2', icon: 'pi pi-check' }
        ]
      });
    }

    return items;
  };

  // Create end template for the menubar
  const createEndTemplate = () => {
    return (
      <div className="flex align-items-center gap-2">
        {navConfig.rightButton && (
          <Button
            label="Action"
            icon="pi pi-plus"
            size="small"
            severity="primary"
          />
        )}
        {navConfig.logOut && (
          <Button
            label="Logout"
            icon="pi pi-sign-out"
            size="small"
            severity="secondary"
            outlined
          />
        )}
        {navConfig.avatar && (
          <Avatar
            label="U"
            size="large"
            shape="circle"
            className="bg-gray-200 text-gray-700"
            style={{
              fontSize: '21px',
              fontFamily: 'Inter, sans-serif',
              color: '#2a3339'
            }}
          />
        )}
      </div>
    );
  };

  const getNavStyle = () => {
    if (navConfig.color === 'Dark') {
      return {
        backgroundColor: '#0047BB',
        color: '#FFFFFF',
        borderBottom: '1px solid #0047BB'
      };
    }
    return {
      backgroundColor: '#FFFFFF',
      color: '#2a3339',
      borderBottom: '1px solid #b2c0c7'
    };
  };

  return (
    <div className="p-4">
      <Card className="mb-4">
        <div className="mb-4">
          <h2 className="text-2xl font-bold mb-2">Figma MCP Global Navigation</h2>
          <p className="text-gray-600 mb-4">
            This Global Navigation component was built using Figma MCP from your selected design with PrimeReact components.
          </p>
        </div>
      </Card>

      {/* Navigation Configuration */}
      <Card className="mb-4">
        <h3 className="text-lg font-semibold mb-3">Navigation Configuration</h3>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Color Theme:</label>
            <Dropdown
              value={navConfig.color}
              options={colorOptions}
              onChange={(e) => handleConfigChange('color', e.value)}
              placeholder="Select color theme"
              className="w-full"
            />
          </div>
        </div>

        <Divider />

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
          <div className="flex align-items-center gap-2">
            <Checkbox
              inputId="header"
              checked={navConfig.header}
              onChange={(e) => handleConfigChange('header', e.checked)}
            />
            <label htmlFor="header" className="text-sm font-medium">Show Header</label>
          </div>

          <div className="flex align-items-center gap-2">
            <Checkbox
              inputId="megaMenu"
              checked={navConfig.megaMenu}
              onChange={(e) => handleConfigChange('megaMenu', e.checked)}
            />
            <label htmlFor="megaMenu" className="text-sm font-medium">Mega Menu</label>
          </div>

          <div className="flex align-items-center gap-2">
            <Checkbox
              inputId="menu"
              checked={navConfig.menu}
              onChange={(e) => handleConfigChange('menu', e.checked)}
            />
            <label htmlFor="menu" className="text-sm font-medium">Menu</label>
          </div>

          <div className="flex align-items-center gap-2">
            <Checkbox
              inputId="tabs"
              checked={navConfig.tabs}
              onChange={(e) => handleConfigChange('tabs', e.checked)}
            />
            <label htmlFor="tabs" className="text-sm font-medium">Show Tabs</label>
          </div>

          <div className="flex align-items-center gap-2">
            <Checkbox
              inputId="avatar"
              checked={navConfig.avatar}
              onChange={(e) => handleConfigChange('avatar', e.checked)}
            />
            <label htmlFor="avatar" className="text-sm font-medium">Show Avatar</label>
          </div>

          <div className="flex align-items-center gap-2">
            <Checkbox
              inputId="rightButton"
              checked={navConfig.rightButton}
              onChange={(e) => handleConfigChange('rightButton', e.checked)}
            />
            <label htmlFor="rightButton" className="text-sm font-medium">Right Button</label>
          </div>
        </div>
      </Card>

      {/* Global Navigation Component */}
      <Card className="mb-4">
        <div className="surface-0 border-bottom-1 border-300" style={getNavStyle()}>
          <Menubar
            model={createMenubarItems()}
            end={createEndTemplate}
            className="border-none bg-transparent px-4 py-2"
            style={{
              backgroundColor: 'transparent',
              color: navConfig.color === 'Dark' ? '#FFFFFF' : '#2a3339'
            }}
          />

          {navConfig.tabs && (
            <div className="px-4 pb-2">
              <TabView
                activeIndex={activeTab}
                onTabChange={(e) => setActiveTab(e.index)}
                className="border-none"
                style={{
                  backgroundColor: 'transparent'
                }}
              >
                {tabItems.map((item, index) => (
                  <TabPanel
                    key={index}
                    header={item.label}
                    headerClassName={navConfig.color === 'Dark' ? 'text-white' : 'text-gray-700'}
                    style={{
                      backgroundColor: 'transparent',
                      border: 'none'
                    }}
                  >
                    <div className="p-4">
                      <h3 className="text-lg font-semibold mb-2">{item.label} Content</h3>
                      <p className="text-gray-600">
                        This is the content for {item.label.toLowerCase()}. You can customize this content as needed.
                      </p>
                    </div>
                  </TabPanel>
                ))}
              </TabView>
            </div>
          )}
        </div>
      </Card>

      {/* Component Details */}
      <Card>
        <div className="mb-3">
          <h3 className="text-lg font-semibold mb-2">Component Details</h3>
          <Divider />
        </div>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm">
          <div className="flex align-items-center gap-2">
            <i className="pi pi-palette text-primary"></i>
            <strong>Color Theme:</strong> {navConfig.color}
          </div>
          <div className="flex align-items-center gap-2">
            <i className="pi pi-tag text-primary"></i>
            <strong>Primary Color:</strong> #0047BB
          </div>
          <div className="flex align-items-center gap-2">
            <i className="pi pi-font text-primary"></i>
            <strong>Font Family:</strong> Inter
          </div>
          <div className="flex align-items-center gap-2">
            <i className="pi pi-list text-primary"></i>
            <strong>Active Tab:</strong> {tabItems[activeTab]?.label || 'None'}
          </div>
        </div>
      </Card>
    </div>
  );
};

export default FigmaMCPGlobalNav;
