import React, { useState } from 'react';
import { Dialog } from 'primereact/dialog';
import { Button } from 'primereact/button';
import { Card } from 'primereact/card';
import { Checkbox } from 'primereact/checkbox';
import { InputText } from 'primereact/inputtext';
import { InputTextarea } from 'primereact/inputtextarea';

const FigmaMCPDialog = () => {
  const [visible, setVisible] = useState(false);
  const [dialogConfig, setDialogConfig] = useState({
    showFooter: true,
    closable: true,
    show2ndButton: true,
    show1stButton: true,
    showIcon: false,
    header: "Dialog Header",
    content: "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum."
  });

  const handleConfigChange = (key, value) => {
    setDialogConfig(prev => ({
      ...prev,
      [key]: value
    }));
  };

  const renderFooter = () => {
    if (!dialogConfig.showFooter) return null;

    return (
      <div className="flex justify-content-end gap-2">
        {dialogConfig.show1stButton && (
          <Button
            outlined
            label="Cancel"
            icon="pi pi-times"
            onClick={() => setVisible(false)}
            className="h-10"
            style={{
              borderColor: '#0047BB',
              color: '#0047BB',
              fontSize: '14px',
              fontWeight: '700',
              fontFamily: 'Inter, sans-serif'
            }}
          />
        )}
        {dialogConfig.show2ndButton && (
          <Button
            label="Confirm"
            icon="pi pi-check"
            onClick={() => setVisible(false)}
            className="h-10"
            style={{
              backgroundColor: '#0047BB',
              borderColor: '#0047BB',
              fontSize: '14px',
              fontWeight: '700',
              fontFamily: 'Inter, sans-serif'
            }}
          />
        )}
      </div>
    );
  };

  return (
    <div className="p-4">
      <Card className="mb-4">
        <div className="mb-4">
          <h2 className="text-2xl font-bold mb-2">Figma MCP Dialog Component</h2>
          <p className="text-gray-600 mb-4">
            This dialog component was built using Figma MCP from your selected design with PrimeReact components.
          </p>

          <Button
            label="Open Dialog"
            icon="pi pi-external-link"
            onClick={() => setVisible(true)}
            className="mb-4"
            style={{
              backgroundColor: '#0047BB',
              borderColor: '#0047BB',
              fontSize: '14px',
              fontWeight: '700',
              fontFamily: 'Inter, sans-serif'
            }}
          />
        </div>
      </Card>

      {/* Dialog Configuration */}
      <Card className="mb-4">
        <h3 className="text-lg font-semibold mb-3">Dialog Configuration</h3>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
          <div className="flex align-items-center gap-2">
            <Checkbox
              inputId="showFooter"
              checked={dialogConfig.showFooter}
              onChange={(e) => handleConfigChange('showFooter', e.checked)}
            />
            <label htmlFor="showFooter" className="text-sm font-medium">Show Footer</label>
          </div>

          <div className="flex align-items-center gap-2">
            <Checkbox
              inputId="closable"
              checked={dialogConfig.closable}
              onChange={(e) => handleConfigChange('closable', e.checked)}
            />
            <label htmlFor="closable" className="text-sm font-medium">Closable</label>
          </div>

          <div className="flex align-items-center gap-2">
            <Checkbox
              inputId="show1stButton"
              checked={dialogConfig.show1stButton}
              onChange={(e) => handleConfigChange('show1stButton', e.checked)}
            />
            <label htmlFor="show1stButton" className="text-sm font-medium">Show First Button</label>
          </div>

          <div className="flex align-items-center gap-2">
            <Checkbox
              inputId="show2ndButton"
              checked={dialogConfig.show2ndButton}
              onChange={(e) => handleConfigChange('show2ndButton', e.checked)}
            />
            <label htmlFor="show2ndButton" className="text-sm font-medium">Show Second Button</label>
          </div>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Header Text:</label>
            <InputText
              value={dialogConfig.header}
              onChange={(e) => handleConfigChange('header', e.target.value)}
              className="w-full"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Content:</label>
            <InputTextarea
              value={dialogConfig.content}
              onChange={(e) => handleConfigChange('content', e.target.value)}
              rows={3}
              className="w-full"
            />
          </div>
        </div>
      </Card>

      {/* Dialog Component */}
      <Dialog
        visible={visible}
        onHide={() => setVisible(false)}
        header={dialogConfig.header}
        footer={renderFooter}
        closable={dialogConfig.closable}
        className="w-full md:w-6"
        style={{
          fontFamily: 'Inter, sans-serif'
        }}
        headerStyle={{
          backgroundColor: '#FFFFFF',
          color: '#3B4951',
          fontSize: '17.5px',
          fontWeight: '700',
          fontFamily: 'Inter, sans-serif',
          padding: '21px'
        }}
        contentStyle={{
          backgroundColor: '#FFFFFF',
          color: '#445660',
          fontSize: '14px',
          fontWeight: '400',
          fontFamily: 'Inter, sans-serif',
          lineHeight: '1.5',
          padding: '0 21px 28px 21px'
        }}
      >
        <p className="m-0">{dialogConfig.content}</p>
      </Dialog>

      {/* Component Details */}
      <Card>
        <div className="mb-3">
          <h3 className="text-lg font-semibold mb-2">Component Details</h3>
        </div>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm">
          <div className="flex align-items-center gap-2">
            <i className="pi pi-palette text-primary"></i>
            <strong>Primary Color:</strong> 
            <div 
              className="w-4 h-4 border-round border-1 border-300" 
              style={{ backgroundColor: '#0047BB' }}
            ></div>
            #0047BB
          </div>
          <div className="flex align-items-center gap-2">
            <i className="pi pi-font text-primary"></i>
            <strong>Font Family:</strong> Inter
          </div>
          <div className="flex align-items-center gap-2">
            <i className="pi pi-tag text-primary"></i>
            <strong>Header Color:</strong> #3B4951
          </div>
          <div className="flex align-items-center gap-2">
            <i className="pi pi-file text-primary"></i>
            <strong>Content Color:</strong> #445660
          </div>
        </div>
      </Card>
    </div>
  );
};

export default FigmaMCPDialog;
