import React, { useState } from 'react';
import { Menubar } from 'primereact/menubar';
import { TabView, TabPanel } from 'primereact/tabview';
import { Avatar } from 'primereact/avatar';
import { Button } from 'primereact/button';
import { Card } from 'primereact/card';
import { Dropdown } from 'primereact/dropdown';
import { Checkbox } from 'primereact/checkbox';
import { Divider } from 'primereact/divider';
import { InputText } from 'primereact/inputtext';

const FigmaMCPGlobalNav = () => {
  const [activeTab, setActiveTab] = useState(0);
  const [navConfig, setNavConfig] = useState({
    color: 'Light',
    header: true,
    megaMenu: false,
    menu: false,
    leftToggleButtons: false,
    avatar: true,
    rightButton: false,
    logOut: false,
    tabs: true
  });

  const colorOptions = [
    { label: 'Light Theme', value: 'Light' },
    { label: 'Dark Theme', value: 'Dark' }
  ];

  const tabItems = [
    { label: 'Dashboard', icon: 'pi pi-home' },
    { label: 'Users', icon: 'pi pi-user' },
    { label: 'Settings', icon: 'pi pi-cog' },
    { label: 'Analytics', icon: 'pi pi-chart' },
    { label: 'Notifications', icon: 'pi pi-bell' }
  ];

  const handleConfigChange = (key, value) => {
    setNavConfig(prev => ({
      ...prev,
      [key]: value
    }));
  };

  // Create menubar items for the navigation
  const createMenubarItems = () => {
    const items = [];

    if (navConfig.header) {
      items.push({
        label: 'Product',
        icon: 'pi pi-th-large',
        className: 'font-bold'
      });
    }

    if (navConfig.megaMenu) {
      items.push({
        label: 'Mega Menu',
        icon: 'pi pi-bars',
        items: [
          [
            {
              label: 'Features',
              items: [
                { label: 'Feature 1', icon: 'pi pi-check' },
                { label: 'Feature 2', icon: 'pi pi-check' },
                { label: 'Feature 3', icon: 'pi pi-check' }
              ]
            },
            {
              label: 'Resources',
              items: [
                { label: 'Documentation', icon: 'pi pi-file' },
                { label: 'Tutorials', icon: 'pi pi-video' },
                { label: 'Support', icon: 'pi pi-question-circle' }
              ]
            }
          ]
        ]
      });
    }

    if (navConfig.menu) {
      items.push({
        label: 'Menu',
        icon: 'pi pi-list',
        items: [
          { label: 'Option 1', icon: 'pi pi-check' },
          { label: 'Option 2', icon: 'pi pi-check' },
          { label: 'Option 3', icon: 'pi pi-check' }
        ]
      });
    }

    return items;
  };

  // Create end template for the menubar
  const createEndTemplate = () => {
    return (
      <div className="flex align-items-center gap-2">
        {navConfig.rightButton && (
          <Button
            label="New"
            icon="pi pi-plus"
            size="small"
            severity="primary"
          />
        )}
        {navConfig.logOut && (
          <Button
            label="Logout"
            icon="pi pi-sign-out"
            size="small"
            severity="secondary"
            outlined
          />
        )}
        {navConfig.avatar && (
          <Avatar
            label="U"
            size="large"
            shape="circle"
            className="bg-primary"
          />
        )}
      </div>
    );
  };

  return (
    <div className="p-4">
      <Card className="mb-4">
        <div className="mb-4">
          <h2 className="text-2xl font-bold mb-2">PrimeReact Global Navigation Demo</h2>
          <p className="text-gray-600 mb-4">
            This demonstrates the PrimeReact Global Navigation using Menubar, TabView, and other PrimeReact components.
          </p>
        </div>
      </Card>

      {/* Navigation Configuration */}
      <Card className="mb-4">
        <h3 className="text-lg font-semibold mb-3">Navigation Configuration</h3>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Theme:</label>
            <Dropdown
              value={navConfig.color}
              options={colorOptions}
              onChange={(e) => handleConfigChange('color', e.value)}
              placeholder="Select theme"
              className="w-full"
            />
          </div>
        </div>

        <Divider />

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
          <div className="flex align-items-center gap-2">
            <Checkbox
              inputId="header"
              checked={navConfig.header}
              onChange={(e) => handleConfigChange('header', e.checked)}
            />
            <label htmlFor="header" className="text-sm font-medium">Show Header</label>
          </div>

          <div className="flex align-items-center gap-2">
            <Checkbox
              inputId="megaMenu"
              checked={navConfig.megaMenu}
              onChange={(e) => handleConfigChange('megaMenu', e.checked)}
            />
            <label htmlFor="megaMenu" className="text-sm font-medium">Mega Menu</label>
          </div>

          <div className="flex align-items-center gap-2">
            <Checkbox
              inputId="menu"
              checked={navConfig.menu}
              onChange={(e) => handleConfigChange('menu', e.checked)}
            />
            <label htmlFor="menu" className="text-sm font-medium">Menu</label>
          </div>

          <div className="flex align-items-center gap-2">
            <Checkbox
              inputId="tabs"
              checked={navConfig.tabs}
              onChange={(e) => handleConfigChange('tabs', e.checked)}
            />
            <label htmlFor="tabs" className="text-sm font-medium">Show Tabs</label>
          </div>

          <div className="flex align-items-center gap-2">
            <Checkbox
              inputId="avatar"
              checked={navConfig.avatar}
              onChange={(e) => handleConfigChange('avatar', e.checked)}
            />
            <label htmlFor="avatar" className="text-sm font-medium">Show Avatar</label>
          </div>

          <div className="flex align-items-center gap-2">
            <Checkbox
              inputId="rightButton"
              checked={navConfig.rightButton}
              onChange={(e) => handleConfigChange('rightButton', e.checked)}
            />
            <label htmlFor="rightButton" className="text-sm font-medium">Right Button</label>
          </div>
        </div>
      </Card>

      {/* Global Navigation Component */}
      <Card className="mb-4">
        <div className="surface-0 border-bottom-1 border-300">
          <Menubar
            model={createMenubarItems()}
            end={createEndTemplate}
            className="border-none bg-transparent px-4 py-2"
          />

          {navConfig.tabs && (
            <div className="px-4 pb-2">
              <TabView
                activeIndex={activeTab}
                onTabChange={(e) => setActiveTab(e.index)}
                className="border-none"
              >
                {tabItems.map((item, index) => (
                  <TabPanel
                    key={index}
                    header={item.label}
                    leftIcon={item.icon}
                  >
                    <div className="p-4">
                      <div className="flex align-items-center gap-2 mb-3">
                        <i className={`${item.icon} text-primary text-xl`}></i>
                        <h3 className="text-lg font-semibold m-0">{item.label}</h3>
                      </div>
                      <p className="text-gray-600 line-height-3">
                        This is the content for {item.label.toLowerCase()}. You can customize this content as needed.
                        The PrimeReact TabView component provides a clean and accessible way to organize content.
                      </p>

                      <Divider />

                      <div className="flex flex-column gap-2">
                        <div className="flex align-items-center gap-2">
                          <i className="pi pi-check-circle text-green-500"></i>
                          <span>PrimeReact {item.label} component</span>
                        </div>
                        <div className="flex align-items-center gap-2">
                          <i className="pi pi-check-circle text-green-500"></i>
                          <span>Fully responsive design</span>
                        </div>
                        <div className="flex align-items-center gap-2">
                          <i className="pi pi-check-circle text-green-500"></i>
                          <span>Accessibility features included</span>
                        </div>
                      </div>
                    </div>
                  </TabPanel>
                ))}
              </TabView>
            </div>
          )}
        </div>
      </Card>

      {/* Component Details */}
      <Card>
        <div className="mb-3">
          <h3 className="text-lg font-semibold mb-2">PrimeReact Navigation Features</h3>
          <Divider />
        </div>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm">
          <div className="flex align-items-center gap-2">
            <i className="pi pi-palette text-primary"></i>
            <strong>Current Theme:</strong> {navConfig.color}
          </div>
          <div className="flex align-items-center gap-2">
            <i className="pi pi-list text-primary"></i>
            <strong>Active Tab:</strong> {tabItems[activeTab]?.label || 'None'}
          </div>
          <div className="flex align-items-center gap-2">
            <i className="pi pi-bars text-primary"></i>
            <strong>Menu Items:</strong> {createMenubarItems().length}
          </div>
          <div className="flex align-items-center gap-2">
            <i className="pi pi-check text-primary"></i>
            <strong>Tabs Enabled:</strong> {navConfig.tabs ? 'Yes' : 'No'}
          </div>
        </div>
      </Card>
    </div>
  );
};

export default FigmaMCPGlobalNav;
